import { Component, OnInit } from '@angular/core';
import { WorkOrderService } from '../work-order.service';
import { WorkOrder } from '../work-order';
import { Router } from '@angular/router';
import { TechnicianService } from '../technician.service';
import { Technician } from '../technician';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-work-orders',
  templateUrl: './work-orders.component.html',
  styleUrls: ['./work-orders.component.css']
})
export class WorkOrdersComponent implements OnInit {
  statuses?: string[];
  workOrders?: WorkOrder[];
  technicians?: Technician[];
  numberRegex = "[0-9]{3}-[0-9]{3}-[0-9]{4}";
  emailRegex = "^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$";
  //email regex for Angular found on: https://www.abstractapi.com/guides/angular-email-validation

  newOrderForm = this.formBuilder.group({
    woNum: <number|null>(null),
    contactName: <string|null>(null),
    email: [<string|null>(null), Validators.pattern(this.emailRegex)],
    contactNumber: [<string|null>(null), Validators.pattern(this.numberRegex)],
    dateReceived: <Date|null>(null),
    technicianId: <number|''|null>(''),
    dateAssigned: <Date|null>(null),
    problem: <string|null>(null),
    status: <string|null>(null),
    dateComplete: <Date|null>(null),
    techComments: <string|null>(null)
  });
  //creates a form with similar values as the WorkOrder interface for easy mapping

  constructor(private workOrderService: WorkOrderService, private technicianService: TechnicianService, private router: Router, private formBuilder: FormBuilder) { }

  /**
   * Calls the getWorkOrders, getTechnicians, and getStatusList
   * functions after the page loads to ensure the call doesn't
   * return empty due the the page load order
  */
  ngOnInit(): void {
    this.getOpenWorkOrders();
    this.getTechnicians();
    this.getStatusList();
  }

  /** 
   * Generates a list of open work orders from a table if records are available
   */
  getOpenWorkOrders(): void {
    this.workOrderService.getFilteredWorkOrders("Assigned").subscribe(workOrders => {
      if (workOrders.length > 0) { 
        this.workOrders = workOrders;
      }
    });
  }

  /**
   * Generates a list of all work orders from a table if records are available
   */
  getAllWorkOrders(): void {
    this.workOrderService.getWorkOrders().subscribe(workOrders => {
      if (workOrders.length > 0) {
        this.workOrders = workOrders;
      }
    });
  }

  /**
   * Filters the workorder query by status value
   */
  getFilteredWorkOrders(status: string) {
    this.workOrderService.getFilteredWorkOrders(status).subscribe(workOrders => {
      if (workOrders.length > 0) {
        this.workOrders = workOrders;
      }
    });
  }

  /**
   * Returns a list of available statuses present in the workOrders
   * table
   */
  getStatusList(): void {
    this.workOrderService.getStatuses().subscribe(statuses => {
      if (statuses.length > 0) {
        this.statuses = statuses;
      }
    });
  }

  /**
   * Returns a list of technicians available from the
   * technicians table
   */
  getTechnicians(): void {
    this.technicianService.getTechnicians().subscribe(technicians => {
      if (technicians.length > 0) {
        this.technicians = technicians;
      }
    });
  }

  /**
   * Re-routes the user to a detailed page for the
   * selected work order
   * @param id
   */
  orderDetail(id: number) {
    this.router.navigateByUrl('orders/order/' + id);
  }

  /**
   * Re-routes the user to a detailed page for the
   * selected technician
   * @param id
   */
  techDetail(id: number) {
    this.router.navigateByUrl('techs/tech/' + id);
  }

  /**
   * This method updates the workorder table by adding a new record
   */
  addWorkOrder() {
    // TODO  get new workorder number thru wo-service
    var current_time = new Date(); // captures current time

    this.newOrderForm.controls.dateReceived.setValue(current_time);
    this.newOrderForm.controls.status.setValue("Assigned");
    //sets expected values for the work order's status and date received values 

    if ((this.newOrderForm.controls.technicianId.value !== '')) {
      this.newOrderForm.controls.dateAssigned.setValue(current_time);
    } else {
      this.newOrderForm.controls.technicianId.setValue(null);
    }
    //sets the assigned date if and only a technician has been set otherwise the tech's id is set to null

    this.getNewWONum();
    // helps set the new wo number since scoping issues will not allow property assignment
  }

  /**
   * Produces a new work order number to assign to a newly created
   * work order and then submits it
   */
  getNewWONum() {
    this.workOrderService.getNewWONum().subscribe( woNum =>
    {
      this.newOrderForm.controls.woNum.setValue(woNum);
      console.log(this.newOrderForm.value);
      //this.workOrderService.createWorkOrder(this.newOrderForm.value as WorkOrder);
      //solution for interface mapping found on: https://stackoverflow.com/questions/44708240/mapping-formgroup-to-interface-object
   });
  }
}
<section class="parent-container">
  <div id="work-orders">
    <h1>Work Orders</h1>

    <!-- action row for user input -->
    <div id="actions">
      <div id="user-action">
        <div class="user-interactive">
          <select class="action-buttons" id="filter" name="filter">
            <option value="none" selected disabled hidden>Status Filter</option>
            <option value="" (click)="getAllWorkOrders()">All</option>
            <option *ngFor="let status of statuses" value="{{status}}" (click)="getFilteredWorkOrders(status)">{{status}}</option>
          </select>
        </div>

        <div class="user-interactive">
          <button class="action-buttons" id="add-new" type="button" onclick="openOrderForm()">Add New</button>
          <div id="modal">
            <!-- try onfocus set [contactName].addAttribute("autofocus") and onblur set [contactName].removeAttribute("autofocus") -->
            <h1>Create a New Work Order</h1>

            <form [formGroup]="newOrderForm" (ngSubmit)="addWorkOrder()">
              <div class="input-field text">
                <label for="contactName">Name: </label>
                <input type="text" name="contactName" id="contactName" formControlName="contactName">
              </div>

              <div class="input-field text">
                <label for="email">E-mail: </label>
                <input type="email" name="email" id="email" formControlName="email" placeholder="jackie_doe@email.com">
                <p class="error" [hidden]="newOrderForm.controls.email.valid || newOrderForm.controls.email.pristine">Your e-mail is invalid. Please check the format and try again</p>
              </div>

              <div class="input-field text">
                <label for="contactNumber">Phone Number: </label>
                <input oninput="hyphenate()" type="tel" name="contactNumber" id="contactNumber" formControlName="contactNumber" placeholder="888-888-8888" />
                <p class="error" [hidden]="contactNumber.valid || contactNumber.pristine">Please make sure your number follows the following pattern: xxx-xxx-xxxx</p>
              </div>

              <div class="input-field drop-down">
                <label for="technicians">Technician: </label>
                <select name="technicians" id="technicians" formControlName="technicianId">
                  <option value="" disabled selected hidden>None selected</option>
                  <option *ngFor="let tech of technicians" value="{{tech.technicianId}}">{{tech.technicianName}}</option>
                </select>
              </div>

              <div class="input-field message">
                <label for="problem">Problem: </label>
                <textarea name="problem" id="problem" rows="7" cols="35" formControlName="problem"></textarea>
              </div>

              <div id="form-btns">
                <input class="action-buttons" id="cancel-btn" type="reset" value="Cancel" onclick="closeOrderForm()">
                <button class="action-buttons" id="submit" type="submit" [disabled]="!newOrderForm.valid">Create</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>

    <!-- work order table to store database info -->
    <div id="orders">
      <table *ngIf="workOrders">
        <tr id="table-header">
          <th>W.O. Number</th>
          <th>Date Received</th>
          <th>Contact Name</th>
          <th>Contact Number</th>
          <th>Email</th>
          <th>Date Assigned</th>
          <th>Technician ID</th>
          <th>Status</th>
          <th>Problem</th>
          <th>Technician Comments</th>
          <th>Date Complete</th>
        </tr>

        <!-- iterable record format -->
        <tr *ngFor="let workOrder of workOrders">
          <td><a routerLink="/orders/order/{{workOrder.woNum}}">{{workOrder.woNum}}</a></td>
          <td>{{workOrder.dateReceived | date: 'shortDate'}}</td>
          <td>{{workOrder.contactName}}</td>
          <td>{{workOrder.contactNumber}}</td>
          <td>{{workOrder.email}}</td>
          <td>{{workOrder.dateAssigned | date: 'shortDate'}}</td>
          <td><a routerLink="/techs/tech/{{workOrder.technicianId}}">{{workOrder.technicianId}}</a></td>
          <td>{{workOrder.status}}</td>
          <td>{{workOrder.problem}}</td>
          <td>{{workOrder.techComments}}</td>
          <td>{{workOrder.dateComplete | date: 'shortDate'}}</td>
        </tr>
      </table>

      <div *ngIf="!workOrders" id="errorMsg">
        <p>There are no work orders available at this time</p>
      </div>
    </div>
  </div>
</section>
