import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';
import { WorkOrderService } from '../work-order.service';
import { WorkOrder } from '../work-order';

@Component({
  selector: 'app-work-order-detail',
  templateUrl: './work-order-detail.component.html',
  styleUrls: ['./work-order-detail.component.css']
})
export class WorkOrderDetailComponent implements OnInit {
  workOrder: WorkOrder | undefined;

  constructor(private route: ActivatedRoute, private workOrderService: WorkOrderService, private location: Location) { }

  /**
   * Calls the getWorkOrder function after the page loads
   * to ensure the call doesn't return empty due the the page load order
  */
  ngOnInit(): void {
    this.getWorkOrder;
  }

  /** 
   * Generates the workorder.noNum by capturing the `:id` from the URI slug
   */
  getWorkOrder(): void {
    const woId = Number(this.route.snapshot.paramMap.get('id'));
    this.workOrderService.getWorkOrder(woId).subscribe(workOrder => this.workOrder = workOrder);
  }

  /**
   *  This method creates a simple route to the previous page
  */
  goBack(): void {
    this.location.back();
  }
}
<section>
  <a id="nav-back" href="" (click)="goBack()">Go Back</a>
  <h1>Work Order #1</h1>

  <div id="workOrder">
      <div class="wo-details" id="overview">
        <h2>Overview</h2>

        <ul>
          <li>Date Received: Jan 12, 2020</li>
          <li>Date Assigned: June 3, 2020</li>
          <li>Status: Open</li>
          <li>Tech ID: 11</li> <!-- make this a hoverable item with a tooltip about the tech -->
        </ul>

        <div tech><!-- create tech info tooltip --></div>
      </div>

      <div class="wo-details" id="contact-info">
        <h2>Contact Info</h2>

        <ul>
          <li>Name: Jane Doe</li>
          <li>Phone Number: 888-888-8888</li>
          <li>Email: customer@email.com	</li>
        </ul>
      </div>

      <div class="wo-details" id="more-info">
        <h2>More Info</h2>
        <h3>Problem</h3>
        <p>Skype</p>

        <h3>Technician Comments</h3>
        <p>It broke</p>

        <p>This work order was closed on: August 23, 2020<p>
      </div>
  </div>
</section>
