import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';
import { TechnicianService } from '../technician.service';
import { Technician } from '../technician';

@Component({
  selector: 'app-technician-detail',
  templateUrl: './technician-detail.component.html',
  styleUrls: ['./technician-detail.component.css']
})

export class TechnicianDetailComponent implements OnInit {
  //@Input() technician?: Technician;
  technician: Technician | undefined;

  constructor(private route: ActivatedRoute, private technicianService: TechnicianService, private location: Location) { }

  /**
   * Calls the getTechnician function after the page loads
   * to ensure the call doesn't return empty due the the page load order
  */
  ngOnInit(): void {
    this.getTechnician;
  }

  /**
   * Generates the technician.id by capturing the `:id` from the URI slug
  */
  getTechnician(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.technicianService.getTechnician(id).subscribe(technician => this.technician = technician);
  }

  /**
   * This method creates a simple route to the previous page
  */
  goBack(): void {
    this.location.back();
  }
}
<section>
  <a id="nav-back" href="techs">Go Back</a>
  <h1>Alfonso K. Rodriguez-Santiago</h1>

  <div id="tech">

    <div class="tech-info" id="personal-details">
      <h2>Tech Info</h2>

      <ul id="info-list">
        <li><span class="identifier">ID No.: </span>1</li>
        <li><span class="identifier">Email: </span><a href="">some.guy@email.com</a></li>
      </ul>
    </div>

    <div class="tech-info" id="work-orders">
      <h2>Work Orders</h2>

      <table>
        <tr class="work-order-card header"><th>W.O. #</th><th>Status</th></tr>
        <tr class="work-order-card"><td class="work-order-id"><a href="">21</a></td><td class="work-order-status">Open</td></tr>
        <tr class="work-order-card"><td class="work-order-id"><a href="">22</a></td><td class="work-order-status">Open</td></tr>
        <tr class="work-order-card"><td class="work-order-id"><a href="">23</a></td><td class="work-order-status">Closed</td></tr>
        <tr class="work-order-card"><td class="work-order-id"><a href="">24</a></td><td class="work-order-status">Open</td></tr>
        <tr class="work-order-card"><td class="work-order-id"><a href="">25</a></td><td class="work-order-status">Open</td></tr>
      </table>

      <p id="errorMsg">Alfonso K. Rodriguez-Santiago has not been assigned to any work orders yet!</p>
    </div>
  </div>
</section>
