            <ul>
              <li><h1 id="tech-name">Alfonso K. Garcia-Rodriguez</h1></li>
              <li>some_guy@email.com</li>
            </ul>
          </div>
          </li> <!-- make this a hoverable item with a tooltip about the tech -->
      </ul>

      <div tech><!-- create tech info tooltip --></div>
    </div>

    <div class="wo-details" id="contact-info">
      <h2>Contact Info</h2>

      <ul>
        <li>Name:</li>
        <li>Phone Number:</li>
        <li>Email:</li>
      </ul>
    </div>

    <div class="wo-details" id="more-info">
      <h2>More Info</h2>
      <h3>Problem</h3>
      <p></p>

      <h3>Technician Comments</h3>
      <p></p>

      <p>This work order was closed on: <p>
    </div>
  </div>
</section>
