import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

@Component({
  selector: 'app-page-not-found',
  templateUrl: './page-not-found.component.html',
  styleUrls: ['./page-not-found.component.css']
})
export class PageNotFoundComponent {
  constructor(private route: ActivatedRoute, private location: Location) { }

  /**
   * This method creates a simple route to the previous page
  */
  goBack(): void {
    this.location.back();
  }
}
<section class="parent-container">
  <div id="page-not-found">
    <h1>Page not found</h1>

    <p>Oops! It looks like you may have tried to access a page that does not exist. But you're in luck! Just choose an option below to continue navigating on this site.</p>

    <div id="user-nav">
      <a id="nav-back" href="" (click)="goBack()">Go Back</a>
      <a id="nav-home" href="/">Home</a>
    </div>
  </div>
</section>
