import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.css']
})
export class WelcomeComponent {

}
<section>
  <h1>Welcome to Lake County Work Orders!</h1>

  <p>
    We're happy to have you here, but you probably want to
    check on something or submit a work order yourself. So choose an option below!
  </p>

  <a routerLink="techs">Meet Our Techs</a>
  <a routerLink="orders">View/Create a Work Order</a>
</section>
